Praxis-Teil  Notwendige Funktionen library(tidyverse) library(magrittr) library(mice) library(miceadds) library(skimr) library(mitml) library(purrr) library(writexl) skimReport = function(data, round = FALSE){ require(skimr) require(dplyr) require(purrr) x = skim(data) N = nrow(data) with(x, { skim_type == &#34;factor&#34; -&gt; fac.mask vars = skim_variable[fac.mask] n.unique = factor.n_unique[fac.mask] strsplit(factor.top_counts, &#34;\, |\:&#34;)[fac.mask] %&gt;% purrr::map(function(x){ as.numeric(x) -&gt; x data.frame(category = x[seq(1, length(x), by = 2)], count = x[seq(2, length(x), by = 2)]) %&gt;% dplyr::mutate(percent = count/N) }) %&gt;% {names(.) = vars;.} %&gt;% map_df(~as.data.frame(.), .id = &#34;variable&#34;) }) -&gt; factors with(x, { skim_type == &#34;numeric&#34; -&gt; num.mask data.frame(variable = skim_variable[num.mask], mean = numeric.mean[num.mask], sd = numeric.sd[num.mask], n = N-x$n_missing[num.mask], n.missing = n_missing[num.mask], perc.missing = n_missing[num.mask]/N) }) -&gt; numerics if (round == TRUE){ within(factors, { percent = round(percent*100, 2) }) -&gt; factors within(numerics, { mean = round(mean, 2) sd = round(sd, 2) perc.missing = round(perc.missing*100, 2) }) -&gt; numerics } dat = list(factors = factors, numerics = numerics) class(dat) = c(&#34;list&#34;, &#34;skimReport&#34;) return(dat) } print.skimReport = function(x){ cat(&#34;Categorial Variables 
